import axios from 'axios'

const salariesUrl = `http://localhost:8080/api/salary`

const getEmployeeSalaries = async (employee) => {
  const response = await axios.get(`${salariesUrl}/${Number(employee.employee_id)}`)
  return response.data
}

function generatePDF(salary) {
    var doc = new jsPDF();


    doc.setFont("times");
    doc.setFontType("bold");
    doc.setFontSize(16);
    doc.text(30, 10, 'International Institue of Information Technology, Bangalore');


    doc.text(10,20,'---------------------------------------------------------------------------------------------------');
    doc.setFontSize(13);
    doc.setFontType('normal');
    doc.setFont("times")
    doc.text(20,40,'Employee Name ');
    doc.text(80,40,employee.first_name);
    doc.text(90,40,employee.last_name)
    doc.text(20,50,'Email_id ');
    doc.text(80,50,employee.email);
    doc.text(20,60,'Title ');
    doc.text(80,60,employee.title);
    doc.text(20,70,'Date ');
    doc.text(80,70,salary.payment_date);
    doc.text(20,80,'Amount ');
    doc.text(80,80,salary.amount);
    doc.text(20,90,'Description ');
    doc.text(80,90,salary.description);
    doc.text(20,100,'---------------------------------------------------------------------------------------------------');
    doc.text(20,110,'This is an autogenerated PDF therefore does not require any signature');

    doc.save('iitb_emp_salary.pdf'); // Save the PDF with name "katara"...
}
const exportObject = { getEmployeeSalaries }

export default exportObject
